package com.naturalno;

public class NaturalNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		       int num = 10, count, total = 0;

		       for(count = 1; count <= num; count++){
		           total = total + count;
		       }

		       System.out.println(total);
		    }
		}